mapper.select:
select count(*)
from member_tbl
where mem_id = #{mem_id}

mapper.update:
update member_tbl

    set MEM_EMAIL = #{mem_email}
    ,   MEM_ZIPCODE = #{mem_zipcode}
    ,   MEM_ADDR = #{mem_addr}
    ,   MEM_ADDR_D = #{mem_addr_d}
    ,   MEM_PHONE = #{mem_phone}
    ,   MEM_NICK = #{mem_nick}
    ,   MEM_ACCEPT_E = #{mem_accept_e}
    ,   MEM_DATE_UP = sysdate

where mem_id = #{mem_id}
